     1â†’import { getExercise } from "../data/workoutDatabase";
     2â†’
     3â†’/**
     4â†’ * Step Types in the linearized workout
     5â†’ */
     6â†’export const StepType = {
     7â†’    BLOCK_START: "block_start", // Indicates the start of a new block
     8â†’    WORK: "work", // Work interval for an exercise
     9â†’    REST: "rest", // Rest interval between exercises
    10â†’    BLOCK_REST: "block_rest", // Longer rest between rounds
    11â†’    WORKOUT_COMPLETE: "workout_complete", // Final step indicating workout is done
    12â†’};
    13â†’
    14â†’/**
    15â†’ * Linearizes a workout template into a flat array of steps.
    16â†’ *
    17â†’ * For each block:
    18â†’ *   For each round (1 to rounds):
    19â†’ *     For each drill in drills:
    20â†’ *       Add WORK step with exercise details and work_sec duration
    21â†’ *       Add REST step with rest_sec duration (skip after last drill of last round)
    22â†’ *
    23â†’ * @param {Object} template - The workout template object
    24â†’ * @returns {Array} Flat array of workout steps
    25â†’ */
    26â†’export function linearizeWorkout(template) {
    27â†’    if (!template || !template.blocks) {
    28â†’        return [];
    29â†’    }
    30â†’
    31â†’    const steps = [];
    32â†’    let stepIndex = 0;
    33â†’    let totalBlocks = template.blocks.length;
    34â†’
    35â†’    template.blocks.forEach((block, blockIndex) => {
    36â†’        const { name: blockName, rounds, drills, work_sec, rest_sec } = block;
    37â†’
    38â†’        // Add block start marker
    39â†’        steps.push({
    40â†’            id: stepIndex++,
    41â†’            type: StepType.BLOCK_START,
    42â†’            blockName,
    43â†’            blockIndex,
    44â†’            totalBlocks,
    45â†’            rounds,
    46â†’            drillCount: drills.length,
    47â†’        });
    48â†’
    49â†’        // Iterate through each round
    50â†’        for (let round = 1; round <= rounds; round++) {
    51â†’            // Iterate through each drill in the round
    52â†’            drills.forEach((drillId, drillIndex) => {
    53â†’                const exercise = getExercise(drillId);
    54â†’                const isLastDrillOfRound = drillIndex === drills.length - 1;
    55â†’                const isLastRound = round === rounds;
    56â†’
    57â†’                // Add WORK step
    58â†’                steps.push({
    59â†’                    id: stepIndex++,
    60â†’                    type: StepType.WORK,
    61â†’                    exerciseId: drillId,
    62â†’                    exerciseName: exercise?.name || drillId,
    63â†’                    instruction: exercise?.instruction || "",
    64â†’                    visualRef: exercise?.visual_ref || null,
    65â†’                    duration: work_sec,
    66â†’                    blockName,
    67â†’                    blockIndex,
    68â†’                    round,
    69â†’                    totalRounds: rounds,
    70â†’                    drillIndex,
    71â†’                    totalDrills: drills.length,
    72â†’                });
    73â†’
    74â†’                // Add REST step (but not after the very last drill of the very last round of the block)
    75â†’                // We always add rest between exercises within a round
    76â†’                if (!isLastDrillOfRound || !isLastRound) {
    77â†’                    steps.push({
    78â†’                        id: stepIndex++,
    79â†’                        type: StepType.REST,
    80â†’                        duration: rest_sec,
    81â†’                        blockName,
    82â†’                        blockIndex,
    83â†’                        round,
    84â†’                        totalRounds: rounds,
    85â†’                        nextExercise: isLastDrillOfRound
    86â†’                            ? getExercise(drills[0])?.name
    87â†’                            : getExercise(drills[drillIndex + 1])?.name,
    88â†’                        isRoundTransition: isLastDrillOfRound,
    89â†’                    });
    90â†’                }
    91â†’            });
    92â†’        }
    93â†’
    94â†’        // Add BLOCK_REST step if defined and not the last block
    95â†’        if (block.block_rest > 0 && blockIndex < totalBlocks - 1) {
    96â†’            // Get the first exercise of the next block for "Next Up" preview
    97â†’            const nextBlock = template.blocks[blockIndex + 1];
    98â†’            const nextBlockFirstDrill = nextBlock?.drills?.[0];
    99â†’            const nextExerciseData = nextBlockFirstDrill ? getExercise(nextBlockFirstDrill) : null;
   100â†’
   101â†’            steps.push({
   102â†’                id: stepIndex++,
   103â†’                type: StepType.BLOCK_REST,
   104â†’                duration: block.block_rest,
   105â†’                blockName,
   106â†’                blockIndex,
   107â†’                nextExercise: nextExerciseData?.name || nextBlockFirstDrill || null,
   108â†’            });
   109â†’        }
   110â†’    });
   111â†’
   112â†’    // Add workout complete step
   113â†’    steps.push({
   114â†’        id: stepIndex++,
   115â†’        type: StepType.WORKOUT_COMPLETE,
   116â†’        workoutName: template.name,
   117â†’    });
   118â†’
   119â†’    return steps;
   120â†’}
   121â†’
   122â†’/**
   123â†’ * Calculate total workout duration in seconds
   124â†’ * @param {Array} steps - Linearized workout steps
   125â†’ * @returns {number} Total duration in seconds
   126â†’ */
   127â†’export function calculateTotalDuration(steps) {
   128â†’    return steps.reduce((total, step) => {
   129â†’        if (
   130â†’            step.type === StepType.WORK ||
   131â†’            step.type === StepType.REST ||
   132â†’            step.type === StepType.BLOCK_REST
   133â†’        ) {
   134â†’            return total + (step.duration || 0);
   135â†’        }
   136â†’        return total;
   137â†’    }, 0);
   138â†’}
   139â†’
   140â†’/**
   141â†’ * Calculate elapsed time up to a specific step
   142â†’ * @param {Array} steps - Linearized workout steps
   143â†’ * @param {number} currentStepIndex - Current step index
   144â†’ * @returns {number} Elapsed time in seconds
   145â†’ */
   146â†’export function calculateElapsedTime(steps, currentStepIndex) {
   147â†’    let elapsed = 0;
   148â†’    for (let i = 0; i < currentStepIndex; i++) {
   149â†’        const step = steps[i];
   150â†’        if (
   151â†’            step.type === StepType.WORK ||
   152â†’            step.type === StepType.REST ||
   153â†’            step.type === StepType.BLOCK_REST
   154â†’        ) {
   155â†’            elapsed += step.duration || 0;
   156â†’        }
   157â†’    }
   158â†’    return elapsed;
   159â†’}
   160â†’
   161â†’/**
   162â†’ * Format seconds to MM:SS display
   163â†’ * @param {number} seconds - Time in seconds
   164â†’ * @returns {string} Formatted time string
   165â†’ */
   166â†’export function formatTime(seconds) {
   167â†’    const mins = Math.floor(seconds / 60);
   168â†’    const secs = seconds % 60;
   169â†’    return `${mins.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
   170â†’}
   171â†’
   172â†’/**
   173â†’ * Get workout summary statistics
   174â†’ * @param {Object} template - Workout template
   175â†’ * @returns {Object} Summary with exercise count, total rounds, estimated duration
   176â†’ */
   177â†’export function getWorkoutSummary(template) {
   178â†’    if (!template || !template.blocks) {
   179â†’        return { exerciseCount: 0, totalRounds: 0, estimatedDuration: 0 };
   180â†’    }
   181â†’
   182â†’    let exerciseCount = 0;
   183â†’    let totalRounds = 0;
   184â†’    let estimatedDuration = 0;
   185â†’    const totalBlocks = template.blocks.length;
   186â†’
   187â†’    template.blocks.forEach((block, index) => {
   188â†’        const { rounds, drills, work_sec, rest_sec, block_rest } = block;
   189â†’        exerciseCount += drills.length;
   190â†’        totalRounds += rounds;
   191â†’
   192â†’        // Each round: (work + rest) * drills, minus the last rest
   193â†’        const roundTime = drills.length * work_sec + (drills.length - 1) * rest_sec;
   194â†’        estimatedDuration += roundTime * rounds + (rounds - 1) * rest_sec;
   195â†’
   196â†’        // Add block rest if it exists and is not the last block
   197â†’        if (block_rest > 0 && index < totalBlocks - 1) {
   198â†’            estimatedDuration += block_rest;
   199â†’        }
   200â†’    });
   201â†’
   202â†’    return {
   203â†’        exerciseCount,
   204â†’        totalRounds,
   205â†’        estimatedDuration,
   206â†’        estimatedDurationFormatted: formatTime(estimatedDuration),
   207â†’    };
   208â†’}
