     1â†’import { useState, useEffect, useCallback } from 'react';
     2â†’
     3â†’const STORAGE_KEY = 'workout_history';
     4â†’
     5â†’/**
     6â†’ * Custom hook to manage workout history with localStorage persistence.
     7â†’ * Handles saving, loading, deleting, and clearing workout history entries.
     8â†’ * @returns {Object} { history, addWorkout, deleteWorkout, clearHistory }
     9â†’ */
    10â†’export default function useWorkoutHistory() {
    11â†’    const [history, setHistory] = useState([]);
    12â†’
    13â†’    // Load history from localStorage on mount
    14â†’    useEffect(() => {
    15â†’        const savedHistory = localStorage.getItem(STORAGE_KEY);
    16â†’        if (savedHistory) {
    17â†’            try {
    18â†’                const parsed = JSON.parse(savedHistory);
    19â†’                if (Array.isArray(parsed)) {
    20â†’                    setHistory(parsed);
    21â†’                } else {
    22â†’                    // Corrupted data - reset to empty array
    23â†’                    setHistory([]);
    24â†’                    localStorage.setItem(STORAGE_KEY, JSON.stringify([]));
    25â†’                }
    26â†’            } catch (e) {
    27â†’                console.warn('Failed to parse workout history', e);
    28â†’                // Reset corrupted data
    29â†’                setHistory([]);
    30â†’                localStorage.setItem(STORAGE_KEY, JSON.stringify([]));
    31â†’            }
    32â†’        }
    33â†’    }, []);
    34â†’
    35â†’    /**
    36â†’     * Save history to localStorage
    37â†’     * @param {Array} newHistory - The updated history array to persist
    38â†’     */
    39â†’    const persistHistory = useCallback((newHistory) => {
    40â†’        try {
    41â†’            localStorage.setItem(STORAGE_KEY, JSON.stringify(newHistory));
    42â†’        } catch (e) {
    43â†’            // Handle localStorage quota exceeded
    44â†’            console.warn('Failed to save workout history to localStorage', e);
    45â†’        }
    46â†’    }, []);
    47â†’
    48â†’    /**
    49â†’     * Add a new workout entry to history
    50â†’     * @param {Object} workout - The workout to add
    51â†’     * @param {string} workout.workoutName - Name of the completed workout
    52â†’     * @param {number} workout.duration - Duration in seconds
    53â†’     * @param {string} [workout.date] - ISO date string (defaults to now)
    54â†’     * @param {string} [workout.weekKey] - Week identifier
    55â†’     * @param {string} [workout.day] - Day identifier
    56â†’     */
    57â†’    const addWorkout = useCallback((workout) => {
    58â†’        const entry = {
    59â†’            id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
    60â†’            date: workout.date || new Date().toISOString(),
    61â†’            workoutName: workout.workoutName,
    62â†’            duration: workout.duration,
    63â†’            ...(workout.weekKey && { weekKey: workout.weekKey }),
    64â†’            ...(workout.day && { day: workout.day }),
    65â†’        };
    66â†’
    67â†’        setHistory((prevHistory) => {
    68â†’            // Add new entry at the beginning (newest first)
    69â†’            const newHistory = [entry, ...prevHistory];
    70â†’            persistHistory(newHistory);
    71â†’            return newHistory;
    72â†’        });
    73â†’    }, [persistHistory]);
    74â†’
    75â†’    /**
    76â†’     * Delete a workout entry by ID
    77â†’     * @param {string} id - The ID of the workout entry to delete
    78â†’     */
    79â†’    const deleteWorkout = useCallback((id) => {
    80â†’        setHistory((prevHistory) => {
    81â†’            const newHistory = prevHistory.filter((entry) => entry.id !== id);
    82â†’            persistHistory(newHistory);
    83â†’            return newHistory;
    84â†’        });
    85â†’    }, [persistHistory]);
    86â†’
    87â†’    /**
    88â†’     * Clear all workout history
    89â†’     */
    90â†’    const clearHistory = useCallback(() => {
    91â†’        setHistory([]);
    92â†’        persistHistory([]);
    93â†’    }, [persistHistory]);
    94â†’
    95â†’    return {
    96â†’        history,
    97â†’        addWorkout,
    98â†’        deleteWorkout,
    99â†’        clearHistory,
   100â†’    };
   101â†’}
   102â†’

